              </menu-dropdown>
              <li @click="showMenu = false">
                <nuxt-link to="/guide"> تست </nuxt-link>
              </li>
            </ul>
          </div>
          <!-- dark and light -->
          <span>
            <button @click="darkModeListener" class="yellow" id="dark">
              <template v-if="bodyTheme">
                <img
                  id="dark"
                  class="w-7"
                  :src="require('~/assets/media/svg/sun.svg')"
                  alt="light"
                />
              </template>
              <template v-else>
                <img
                  id="light"
                  class="w-7"
                  :src="require('~/assets/media/svg/moon.svg')"
                  alt="light"
                />
              </template>
            </button>
          </span>
        </div>
      </transition>
    </template>
  </div>
</template>

<script>
export default {
  name: "IndexPage",
  data() {
    return {
      showMenu: false,
      bodyTheme: false,
    };
  },
  methods: {
    darkModeListener() {
      document.querySelector("html").classList.toggle("dark");
      this.bodyTheme == false
        ? (this.bodyTheme = true)
        : (this.bodyTheme = false);
    },
  },
  mounted() {
    window.addEventListener("scroll", function () {
      document
        .getElementById("main_menu")
        .classList.toggle("main-menu", window.scrollY > 200);
    });
  },
};
</script>

